    private void addbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addbtnActionPerformed
        // TODO add your handling code here:
        String nLabel=nametxt.getText();
        String rLabel=ranktxt.getText();
        String cLabel=cntxt.getText();
        String atten;
         if(jRadioButton1.isSelected())
      {
          atten="Present";
      }
      else
      {
          atten="Absent";
      }
        //String aLabel=atxt.getText();
        
        DefaultTableModel dt=(DefaultTableModel)jTable1.getModel();
        dt.addRow(new Object[]{nametxt.getText(),ranktxt.getText(),cntxt.getText(),atten});
        JOptionPane.showMessageDialog(this, "Record Addeded Successfully...");
            nametxt.setText("");
            ranktxt.setText("");
            cntxt.setText("");
           // atxt.setText("");
    }//GEN-LAST:event_addbtnActionPerformed

