    private void detbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_detbtnActionPerformed
        // TODO add your handling code here:
        
        DefaultTableModel dt=(DefaultTableModel)jTable1.getModel();
        int index=jTable1.getSelectedRow();
        dt.removeRow(index);
            JOptionPane.showMessageDialog(this, "Record Delete");
            nametxt.setText("");
            ranktxt.setText("");
            cntxt.setText("");
            
            addbtn.setEnabled(true);
            editbtn.setEnabled(false);
        
    }//GEN-LAST:event_detbtnActionPerformed

    private void jTable1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable1MouseClicked
        // TODO add your handling code here:
        DefaultTableModel model = (DefaultTableModel)jTable1.getModel();
       int selectRow = jTable1.getSelectedRow();
       nametxt.setText(model.getValueAt(selectRow, 0).toString());
       ranktxt.setText(model.getValueAt(selectRow, 1).toString());
       cntxt.setText(model.getValueAt(selectRow, 2).toString());
       addbtn.setEnabled(false);
       editbtn.setEnabled(true);
    }//GEN-LAST:event_jTable1MouseClicked

    private void editbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_editbtnActionPerformed
        // TODO add your handling code here:
           String nLabel=nametxt.getText();
        String rLabel=ranktxt.getText();
        String cLabel=cntxt.getText();
        //String aLabel=atxt.getText();
        DefaultTableModel dt=(DefaultTableModel)jTable1.getModel();
        int index=jTable1.getSelectedRow();
        dt.setValueAt(nLabel, index, 0);
        dt.setValueAt(rLabel, index, 1);
        dt.setValueAt(cLabel, index, 2);
        JOptionPane.showMessageDialog(this, "Updated");
    }//GEN-LAST:event_editbtnActionPerformed

